#include "collisions.h"

Collisions:: Collisions(int numScenes){

    scenecNum = numScenes;
    loadGrid();

}

int Collisions:: testColision(Vector2 pos, int scene){

    int x = (int) pos.x/10;
    int y = (int) pos.y/10;

    if(x < 0){
        return 1;
    }
    else if(x > 66){
        return 1;
    }
    else if(y < 0){
        return 1;
    }
    else if(y > 42){
        return 1;
    }
    
    std:: cout << "Posição x: " << (int)(x)+1 << " || " << "Posição y: " << (int)y+1 << " Col: " << grid[2992*scene+y*67+x] << " "<< (2992*scene+y*67+x) << "\n";
    
    return grid[2992*scene+y*67+x];
    
    //return x;
}

void Collisions:: setInGrid(int x, int y, int set, int cena){
 
    x = (int) x/10;
    y = (int) y/10;
    grid[2992*cena+y*67+x]= set;
    //std::cout << "Setado: " << x << " " << y << " " << grid[y*67+x] << "\n" ;

}

